document.addEventListener("DOMContentLoaded",() =>{const container = document.querySelector(".gallery");const items = Array.from(container.querySelectorAll(".gallery__item"));const shuffled = items.sort(() => 0.5 - Math.random());container.innerHTML = "";shuffled.forEach(item => container.appendChild(item));shuffled.forEach((el,index) =>{setTimeout(() =>{el.style.opacity = 1;},index * 120);});});